void STrackCutStudy::End() {

  // announce method
  cout << "    Ending:" << endl;
  SetHistStyles();
  CreatePlots();
  SaveHists();

  // close files
  fOut  -> cd();
  fOut  -> Close();
  fInEO -> cd();
  fInEO -> Close();
  fInPU -> cd();
  fInPU -> Close();
  cout << "      Closed files.\n"
       << "  Finished track cut study!\n"
       << endl;
  return ;

}  // end End()



